pragma solidity ^0.4.18;


import "zeppelin/ownership/Ownable.sol";
import "zeppelin/payment/PullPayment.sol";


/// @title Base Contract for Wolsuki. Holds all common structs, events and base variables.
/// @author Wolsuki (https://www.wolsuki.com)
contract WolsukiBase is Ownable, PullPayment {

    /// @dev When a Pool is created or opened, players can start placing their bets
    /// until the Pool is ended. A ended Pool cannot be reopened and can only be ended
    /// after a period. A pool can only be ended if and only if its fixture has ended.
    struct Pool {
        // A mapping from a player's address to the value of a bet that the player
        // places for this pool.
        mapping(address => uint256) playerBets;

        // A mapping from a player's address to whether the player has claimed
        // prize if winning.
        mapping(address => bool) claimed;

        // List of all players participating in this pool.
        address[] players;

        // Bet of the host when he/she creates the pool
        address host;

        // The outcome / winning guess number of a pool. If a player has placed a bet which
        // is the same as the outcome, that player is considered as a winner and is entitled
        // the claim the reward.
        uint256 fixtureId;

        // Value of what the host puts in to bet against other players.
        uint256 hostBet;

        // Maximum total of bets all players are allowed to bet against the host.
        uint256 maxTotal;

        // Total of all bets in the pool except for host's bet.
        uint256 total;

        // Outcome of the fixture that the host predicts it's unlikely going to happen.
        uint8 unlikelyOutcome;
    }

    /// @dev When a fixture is created, users can start opening their own pools from the fixture
    /// util the fixture is closed. A closed fixture cannot be reopened and can only be closed
    /// after a period. The result of the fixture is queried from SportMonks api and cannot
    /// be altered by anyone. Furthermore, only contract owner can create a fixture and contract
    /// owner needs to make sure a fixture is linked properly to a fixture on SportMonks.
    struct Fixture {
        // When the fixture can be ended. Calling the fixture to end before this time will throw
        // an error.
        uint256 closedAt;

        // When the fixture can be ended. Calling the fixture to end before this time will throw
        // an error.
        uint256 endedAt;

        // Fixture's id from api provider.
        string externalId;

        // Status of a fixture. It can be any of the following values:
        //  1 => home team wins
        //  2 => away team wins
        //  3 => draw
        uint8 status;

        // Whether a fixture is ended. This value can only be changed from false to true,
        // which means if a fixture is ended, it cannot be reopened.
        bool ended;
    }

    /// @dev List of all fixtures. Only the owner of the contract can open a new fixture.
    Fixture[] public fixtures;

    /// @dev List of all pools. Anyone can open a new pool.
    Pool[] public pools;


    /// @dev The payable value in a transaction must be greater than 0.001 ETH and
    /// smaller than 100 ETH in order for the transaction to be performed.
    /// Otherwise, it will be reverted.
    modifier validValue() {
        require(msg.value > 1000000000000000 && msg.value < 100000000000000000000);
        _;
    }

    /// @dev A valid guess for a bet is between 1 and 3 where:
    ///   1 => home team wins
    ///   2 => away team wins
    ///   3 => draw
    /// If a player tries to place a bet whose guess is either greater than 3
    /// or smaller than 1, the transaction will be reverted.
    /// For example, a guess of 0 will result in transaction error.
    /// Similarly if the player tries to place a bet with guess 10 ( > 3), an error
    /// will be thrown for that transaction as well.
    modifier validGuess(uint8 _guess) {
        require(1 <= _guess && _guess <= 3);
        _;
    }


    /// @dev Helper function to query a bet's value in a pool
    /// @param _poolId id of the pool that is queried from
    /// @param _player address of player the bet belongs to
    /// @return the bet's value
    function queryBetValue(uint256 _poolId, address _player)
        public
        constant
        returns (uint256)
    {
        Pool storage pool = pools[_poolId];
        if (_player == pool.host)
            return pool.hostBet;
        return pool.playerBets[_player];
    }

    /// @dev Helper function to query whether a bet is claimed
    /// @param _poolId id of the pool that is queried from
    /// @param _player address of player the bet belongs to
    /// @return the Bet object
    function queryBetClaimed(uint256 _poolId, address _player)
        public
        constant
        returns (bool)
    {
        Pool storage pool = pools[_poolId];
        return pool.claimed[_player];
    }

    /// @dev Helper function to query list of players in a pool
    /// @param _poolId id of the pool that is queried from
    /// @return list of players' addresses
    function queryPlayers(uint256 _poolId)
        public
        constant
        returns (address[])
    {
        Pool storage pool = pools[_poolId];
        return pool.players;
    }
}

