pragma solidity ^0.4.18;


import "zeppelin/math/SafeMath.sol";
import "./WolsukiFactory.sol";
import "./WolsukiValet.sol";


/// @title Core contract for Wolsuki. Holds all functions related to any single pool.
/// @author Wolsuki (https://www.wolsuki.com)
contract WolsukiCore is WolsukiFactory, WolsukiValet {
    using SafeMath for uint256;

    /// @dev BetPlaced event is emitted whenever a player places a bet.
    event BetPlaced(uint256 _poolId, address _player, uint256 _value);

    /// @dev RewardClaimed event is emitted whenever a player claims a reward.
    event RewardClaimed(uint256 _poolId, address _player);

    /// @dev PoolOpened event is emitted whenever a user opens a pool.
    event PoolOpened(
        uint256 _poolId,
        address _host,
        uint256 _fixture,
        uint64 _ratio,
        uint8 _unlikelyOutcome
    );


    /// @dev payable constructor
    function WolsukiCore() public payable {}

    // @dev checks if a player won in a pool. A player wins when the pool is ended and
    // the player's bet matches the outcome of the pool's fixture.
    // @param _poolId id of the pool to be checked.
    // @param _player address of the player to be checked.
    // @return whether the player won the pool
    function won(uint256 _poolId, address _player)
        public
        constant
        returns (bool)
    {
        Pool storage pool = pools[_poolId];
        Fixture storage fixture = fixtures[pool.fixtureId];

        if (!fixture.ended)
            return false;

        // If sender is host and the unlikely outcome does not happen, the host won
        if (_player == pool.host) {
            if (pool.unlikelyOutcome != fixture.status)
                return true;
        } else {
            if (pool.unlikelyOutcome == fixture.status) {
                // Check if the player actually bet in this pool. If not, return false
                if (pool.playerBets[_player] > 0)
                    return true;
            }
        }
        return false;
    }

    // @dev places a bet in the specified pool. A player can only place a bet once
    // in a pool with a valid gate.
    // @param _poolId id of the pool the player wants to place a bet
    // @param _gate the gate number the player wants to place a bet on
    function play(uint256 _poolId)
        public
        payable
        validValue
    {
        Pool storage pool = pools[_poolId];
        Fixture storage fixture = fixtures[pool.fixtureId];
        // fixture must still be opened, sender must not be host
        require(now > fixture.closedAt && msg.sender != pool.host);
        // bet must not exceed pool's limit
        require(pool.total.add(msg.value) <= pool.maxTotal);

        uint256 bet = pool.playerBets[msg.sender];
        // player must not place a bet in this pool before
        require(bet == 0);

        pool.playerBets[msg.sender] = msg.value;
        pool.players.push(msg.sender);
        pool.total = pool.total.add(msg.value);

        BetPlaced(_poolId, msg.sender, msg.value);
    }

    // @dev claims prize if won or claims bet back if no winners. After the pool is ended,
    // a winner needs to claim the winner's prize to be able to withdraw it from their wallet
    // later. If there are no winners, in order to claim their bets back, a player will also
    // need to call this function.
    // @param _poolId id of the pool to claim from
    function claim(uint256 _poolId)
        public
    {
        Pool storage pool = pools[_poolId];
        Fixture storage fixture = fixtures[pool.fixtureId];
        // fixture must have been ended and player has not claimed prize
        require(fixture.ended && !pool.claimed[msg.sender]);

        pool.claimed[msg.sender] = true;

        if (pool.host == msg.sender) {
            if (pool.unlikelyOutcome != fixture.status) {
                uint256 hostPayout = pool.hostBet.add(pool.total);
                asyncSend(msg.sender, hostPayout);
            } else {
                uint256 hostLost = (pool.hostBet.mul(pool.total)).div(pool.maxTotal);
                uint256 hostRemaining = pool.hostBet - hostLost;
                asyncSend(msg.sender, hostRemaining);
            }
        } else {
            if (pool.unlikelyOutcome == fixture.status) {
                uint256 playerReward = (pool.playerBets[msg.sender].mul(pool.maxTotal)).div(pool.maxTotal);
                uint256 playerPayout = pool.playerBets[msg.sender].add(playerReward);
                asyncSend(msg.sender, playerPayout);
            }
        }

        RewardClaimed(_poolId, msg.sender);
    }

    // @dev places a bet in the specified pool. A player can only place a bet once
    // in a pool with a valid gate.
    // @param _poolId id of the pool the player wants to place a bet
    // @param _gate the gate number the player wants to place a bet on
    function createPool(uint256 _fixtureId, uint64 _ratio, uint8 _unlikelyOutcome)
        public
        payable
        validValue
        validGuess(_unlikelyOutcome)
        returns (uint256)
    {
        // The real ratio will 100 / _ratio. To restrict real ratio between 1:0.01 to 1:100,
        // we limit the _ration param between 1 and 10000
        require(1 <= _ratio && _ratio <= 10000);

        Fixture storage fixture = fixtures[_fixtureId];
        // fixture must not be closed
        require(now > fixture.closedAt);

        uint256 maxTotal = (msg.value.mul(_ratio)).div(100);

        // @dev the default status is 0 but this does not matter as long as the fixture is opened.
        // One should always check if the fixture is ended to know that the status is final.
        Pool memory newPool = Pool({
            players: new address[](0), // an empty list of players
            host: msg.sender, // host's address is sender's address
            fixtureId: _fixtureId,
            hostBet: msg.value, // host's bet is the eth sent along
            maxTotal: maxTotal, // max total of bets calculated from host's bet and ratio
            total: 0, // by default, no one has bet
            unlikelyOutcome: _unlikelyOutcome
        });

        // adds newly created fixture to the fixture list
        uint256 newPoolId = pools.push(newPool) - 1;

        // emit the fixture opened event with the fixture's id
        PoolOpened(newPoolId, msg.sender, _fixtureId, _ratio, _unlikelyOutcome);

        return newPoolId;
    }
}
